namespace exer39{public partial class Form1:Form{public Form1(){InitializeComponent();}private void Form1_Load(object sender,EventArgs e){string massaStr=PromptValor("Digite a massa inicial em gramas:");if(double.TryParse(massaStr,out double massa)){double massaAtual=massa;int tempo=0;while(massaAtual>=0.05){massaAtual/=2;tempo+=50;}MessageBox.Show($"Tempo necessário: {tempo} segundos\nMassa final: {massaAtual:F6} gramas");}}private string PromptValor(string msg){var form=new Form{Text="Input",Width=300,Height=150,StartPosition=FormStartPosition.CenterParent};var label=new Label{Left=20,Top=20,Text=msg,Width=260};var textBox=new TextBox{Left=20,Top=50,Width=260};var okButton=new Button{Text="OK",Left=120,Width=100,Top=80,DialogResult=DialogResult.OK};form.Controls.Add(label);form.Controls.Add(textBox);form.Controls.Add(okButton);form.AcceptButton=okButton;return form.ShowDialog()==DialogResult.OK?textBox.Text:string.Empty;}}}
