namespace exer40{public partial class Form1:Form{public Form1(){InitializeComponent();}private void Form1_Load(object sender,EventArgs e){string num1Str=PromptValor("Digite o primeiro número:");string num2Str=PromptValor("Digite o segundo número:");if(int.TryParse(num1Str,out int n1)&&int.TryParse(num2Str,out int n2)){int resultado=0;for(int i=0;i<n2;i++)resultado+=n1;MessageBox.Show($"{n1} x {n2} = {resultado}");}}private string PromptValor(string msg){var form=new Form{Text="Input",Width=300,Height=150,StartPosition=FormStartPosition.CenterParent};var label=new Label{Left=20,Top=20,Text=msg,Width=260};var textBox=new TextBox{Left=20,Top=50,Width=260};var okButton=new Button{Text="OK",Left=120,Width=100,Top=80,DialogResult=DialogResult.OK};form.Controls.Add(label);form.Controls.Add(textBox);form.Controls.Add(okButton);form.AcceptButton=okButton;return form.ShowDialog()==DialogResult.OK?textBox.Text:string.Empty;}}}
