namespace exer43{public partial class Form1:Form{public Form1(){InitializeComponent();}private void Form1_Load(object sender,EventArgs e){string input=PromptValor("Digite números (9999 para encerrar, separados por espaço):");string[] nums=input.Split(' ');int maior=int.MinValue;foreach(var num in nums){if(int.TryParse(num,out int val)&&val!=9999)maior=Math.Max(maior,val);}MessageBox.Show($"Maior número: {maior}");} private string PromptValor(string msg){var form=new Form{Text="Input",Width=400,Height=150,StartPosition=FormStartPosition.CenterParent};var label=new Label{Left=20,Top=20,Text=msg,Width=360};var textBox=new TextBox{Left=20,Top=50,Width=360};var okButton=new Button{Text="OK",Left=220,Width=100,Top=80,DialogResult=DialogResult.OK};form.Controls.Add(label);form.Controls.Add(textBox);form.Controls.Add(okButton);form.AcceptButton=okButton;return form.ShowDialog()==DialogResult.OK?textBox.Text:string.Empty;}}}
