namespace exer47{public partial class Form1:Form{public Form1(){InitializeComponent();}private void Form1_Load(object sender,EventArgs e){string input=PromptValor("Digite quantas vezes usou restaurante (separado por espaço):");string[] nums=input.Split(' ');int menos10=0,entre10e15=0,mais15=0,total=0;foreach(var num in nums){if(int.TryParse(num,out int val)){total++;if(val<10)menos10++;else if(val>=10&&val<=15)entre10e15++;else if(val>15)mais15++;}}double p1=(menos10*100.0)/total;double p2=(entre10e15*100.0)/total;double p3=(mais15*100.0)/total;MessageBox.Show($"<10 vezes: {p1:F1}%\n10-15 vezes: {p2:F1}%\n>15 vezes: {p3:F1}%");} private string PromptValor(string msg){var form=new Form{Text="Input",Width=400,Height=150,StartPosition=FormStartPosition.CenterParent};var label=new Label{Left=20,Top=20,Text=msg,Width=360};var textBox=new TextBox{Left=20,Top=50,Width=360};var okButton=new Button{Text="OK",Left=220,Width=100,Top=80,DialogResult=DialogResult.OK};form.Controls.Add(label);form.Controls.Add(textBox);form.Controls.Add(okButton);form.AcceptButton=okButton;return form.ShowDialog()==DialogResult.OK?textBox.Text:string.Empty;}}}
