namespace exer49{public partial class Form1:Form{public Form1(){InitializeComponent();}private void Form1_Load(object sender,EventArgs e){string nome1=PromptValor("Nome pessoa 1:");string altura1Str=PromptValor("Altura pessoa 1 (m):");string peso1Str=PromptValor("Peso pessoa 1 (kg):");string nome2=PromptValor("Nome pessoa 2:");string altura2Str=PromptValor("Altura pessoa 2 (m):");string peso2Str=PromptValor("Peso pessoa 2 (kg):");if(double.TryParse(altura1Str,out double alt1)&&double.TryParse(peso1Str,out double p1)&&double.TryParse(altura2Str,out double alt2)&&double.TryParse(peso2Str,out double p2)){string maisAlta=alt1>alt2?$"{nome1} ({alt1:F2}m)":$"{nome2} ({alt2:F2}m)";string maisPesada=p1>p2?$"{nome1} ({p1:F1}kg)":$"{nome2} ({p2:F1}kg)";MessageBox.Show($"Mais pesada: {maisPesada}\nMais alta: {maisAlta}");}} private string PromptValor(string msg){var form=new Form{Text="Input",Width=300,Height=150,StartPosition=FormStartPosition.CenterParent};var label=new Label{Left=20,Top=20,Text=msg,Width=260};var textBox=new TextBox{Left=20,Top=50,Width=260};var okButton=new Button{Text="OK",Left=120,Width=100,Top=80,DialogResult=DialogResult.OK};form.Controls.Add(label);form.Controls.Add(textBox);form.Controls.Add(okButton);form.AcceptButton=okButton;return form.ShowDialog()==DialogResult.OK?textBox.Text:string.Empty;}}}
